package ar.org.fitc.test.integration.math;

import java.io.*;
import java.math.BigDecimal;
import java.math.BigInteger;
import java.math.MathContext;
import java.math.RoundingMode;
import java.util.Arrays;
/**
 * This class uses some of the BigDecimal methods. 
 * Makes use of all constructors and then it probes some methods.
 */ 

class TestBigDecimal {
	private static PrintWriter pw;

	public static BigDecimal bd = null;

	String bigint;

	public static final int QUANTITY = 16;

	private static int iteration = 0;

	public TestBigDecimal(BigDecimal[][] list, boolean flag, String file)
			throws FileNotFoundException {
		pw = new PrintWriter(file);
		if (flag) {
			bigint = "4894253189745618756123189756135679";
			char[] charin = { '1', '5', '6', '8', '4', '3', '2', '1', '4', '5',
					'6', '9', '8', '7', '1', '0', '5', '4', '7', '9', '1', '5',
					'2', '3', '6', '4', '9' };
			list[0][0] = new BigDecimal(new BigInteger(bigint));
			list[0][1] = new BigDecimal(new BigInteger(bigint), 55);
			list[0][2] = new BigDecimal(new BigInteger(bigint), 55,
					MathContext.DECIMAL128);
			list[0][3] = new BigDecimal(new BigInteger(bigint),
					MathContext.DECIMAL64);
			list[0][4] = new BigDecimal(charin);
			list[0][5] = new BigDecimal(charin, 3, 10);
			list[0][6] = new BigDecimal(charin, 3, 10, MathContext.DECIMAL32);
			list[0][7] = new BigDecimal(charin, new MathContext(556));
			list[0][8] = new BigDecimal(-3.87763879679E155d);
			list[0][9] = new BigDecimal(3.87763879679E155d, new MathContext(66,
					RoundingMode.FLOOR));
			list[0][10] = new BigDecimal(189153);
			list[0][11] = new BigDecimal(189153, new MathContext(198,
					RoundingMode.CEILING));
			list[0][12] = new BigDecimal(92233723685477507L);
			list[0][13] = new BigDecimal(-92233723685477507L, new MathContext(
					66, RoundingMode.HALF_DOWN));
			list[0][14] = new BigDecimal(
					"-489414860481050000454015102315484231088888888756423101015604231048945673330415475604444444870123150487650123");
			list[0][15] = new BigDecimal(
					"489414860481050000454015102315484231088888888756423101015604231048945673330415475604444444870123150487650123",
					new MathContext(198, RoundingMode.HALF_UP));

		}
		copyOutput(list);
		pw.flush();
		pw.close();
	}

	private void copyOutput(BigDecimal[][] list) {

		for (int i = 0; i < list[0].length; i++) {
			try {
				list[1][i] = integration(list[0][i]);
			} catch (Throwable e) {
				e.printStackTrace();
			}
		}

		writeInputOutPut(list);
	}

	public void writeInputOutPut(BigDecimal[][] list) {
		pw
				.println("---------------- INPUT -> OUTPUT -----------------------");

		for (int i = 0; i < list[0].length; i++) {
			pw.println("Input: " + list[0][i] + " -> Output: "
					+ list[1][i]);
		}
	}

	public static BigDecimal[][] transformation(BigInteger[][] list) {
		BigDecimal[][] result = new BigDecimal[2][list[0].length];
		for (int i = 0; i < list[0].length; i++) {
			result[0][i] = new BigDecimal(list[1][i]);
		}
		return result;
	}

	public static void main(String[] args) throws FileNotFoundException {
		BigDecimal[][] list = new BigDecimal[2][QUANTITY];
		new TestBigDecimal(list, true, "c:\\asd.txt");
		pw.println("Input:");
		pw.println(Arrays.toString(list[0]));
		pw.println("Output:");
		pw.println(Arrays.toString(list[1]));
	}

	protected static BigDecimal integration(BigDecimal a)
			throws FileNotFoundException {
		pw.println("---------------- Paso a Paso ----------------");
		iteration++;
		if (a == null) {
			pw.print("Iteration = " + iteration);
			return null;
		}

		a = a.add(new BigDecimal("9.693583160302274182E-804"));
		pw.println("x=x.add(BigDecimal): " + a);
		a = a.subtract(a.ulp());
		pw.println("x=x.subtract(ulp()): " + a);
		a = a.max(a.subtract(a.ulp()));
		pw.println("x=x.max(subtract(ulp())): " + a);
		a = a.min(a.subtract(a.ulp()));
		pw.println("x=x.min(subtract(ulp())): " + a);

		a = a.divide(new BigDecimal("5000000000000000000"));
		pw.println("x=x.divide(BigDecimal): " + a);
		a = a.movePointRight(a.precision());
		pw.println("x=x.movePointRight(precision): " + a);
		a = a.movePointLeft(186);
		pw.println("x=x.movePointLeft(int): " + a);
		a = a.stripTrailingZeros();
		pw.println("x=x.stripTrailingZeros(): " + a);
		a = a
				.multiply(new BigDecimal(
						"."));
		pw.println("x=x.multiply(): " + a);
		BigDecimal[] dr = a
				.divideAndRemainder(new BigDecimal(
						"."));
		pw.println("x=x.divideAndRemainder[0](BigDecimal): " + dr[0]);
		pw.println("x=x.divideAndRemainder[1](BigDecimal): " + dr[1]);

		a = a.divide((new BigDecimal("5000000000000")));
		pw.println("x=x.divide(BigDecimal): " + a);
		a = a
				.remainder((new BigDecimal(
						".")));
		pw.println("x=x.divide(BigDecimal): " + a);
		a = a.negate();
		pw.println("x=x.negate(): " + a);
		pw.println("x=x.abs(): " + a.abs());
		a = a.plus();
		pw.println("x=x.plus(): " + a);// ------
		if (a.compareTo(a.abs()) == 0) {
			a = BigDecimal.ZERO;
		} else {
			if (a.compareTo(a.abs()) == 1) {
				a = BigDecimal.ONE;
			} else {
				if (a.compareTo(a.abs()) == -1) {
					a = new BigDecimal("-1");
				}
			}
		}
		pw.println("x.compareTo(): " + a);
		a = a.scaleByPowerOfTen(53);
		pw.println("x=x.scaleByPowerOfTen(int): " + a);
		a = a.divide(new BigDecimal("47891656317486"), new MathContext(
				"precision=743 roundingMode=FLOOR"));
		pw.println("x=x.divide(BigDecimal,MathContext): " + a);
		a = a.pow(25);
		pw.println("x=x.pow(int): " + a);
		a = a
				.multiply(
						new BigDecimal(
								"99999999999988888888844444444477777777555555555666666333332222222111111100000000140140000033333354884489487460444123041104784707776108888888884444444447777777755555555566666633333222222211111110000000014014000003333335488448948746044412304110478470777610.048016012301488888796889444000771001451230232031431111111032415064176847899999940511102486187410140132111118497"),
						new MathContext("precision=7435 roundingMode=HALF_UP"));
		pw.println("x=x.divide(BigDecmial,MathContext): " + a);
		// test for toString
		pw.println("x=x.toPlainString: " + a.toPlainString());
		pw.println("x=x.toString: " + a.toString());
		pw.println("x=x.toEngineeringString: "
				+ a.toEngineeringString());

		// mathcontext
		a = a.round(new MathContext("precision=746 roundingMode=UP"));
		pw.println("x=x.round(MathContext): " + a);
		a = a.abs(new MathContext("precision=744 roundingMode=DOWN"));
		pw.println("x=x.abs(MathContext): " + a);
		a = a
				.add(
						new BigDecimal(
								"."),
						new MathContext("precision=743 roundingMode=HALF_DOWN"));
		pw.println("x=x.add(BigDecmial,MathContext): " + a);
		a = a
				.divide(
						new BigDecimal(
								"9048900510488888889048904501231208940561231899905104888812318999051048888888.9048904501231208940561231899900123120894056123189990510488888889048904501231208940561231899905104888888890489045012312089405612318999051048888888904890450123120894056123189990510488888889048904501239051048888888904890412318999051048888888"),
						5);
		pw.println("x=x.divide(BigDecmial,int): " + a);

		a = a.negate(new MathContext("precision=743 roundingMode=HALF_DOWN"));
		pw.println("x=x.negate(MathContext): " + a);
		try {
			a = a.divide(new BigDecimal("4894053400049"), 489, 3);
			pw
					.println("x=x.divide(BigDecimal divisor, int scale, int roundingMode) : "
							+ a);
		} catch (Throwable e) {
		}

		try {
			a = a.divide(new BigDecimal("4886415E-99"), 247,
					RoundingMode.HALF_DOWN);
			pw
					.println("x=x.divide(BigDecimal divisor, int scale, RoundingMode roundingMode) : "
							+ a);
		} catch (Throwable e) {
		}

		try {
			a = a.divide(new BigDecimal("887645489E+99"),
					RoundingMode.HALF_EVEN);
			pw
					.println("x=x.divide(BigDecimal divisor, RoundingMode roundingMode) : "
							+ a);
		} catch (Throwable e) {
		}

		try {
			dr = a
					.divideAndRemainder(
							(new BigDecimal(
									".")),
							new MathContext(
									"precision=74353 roundingMode=FLOOR"));
		} catch (Throwable e) {
		}

		a = dr[0];

		pw
				.println("x=x.divideAndRemainder(BigDecimal divisor, MathContext mc) [0]: "
						+ dr[0]);
		a = dr[1];
		pw
				.println("x=x.divideAndRemainder(BigDecimal divisor, MathContext mc) [1] : "
						+ dr[1]);
		a = a.setScale(999, 3);

		pw.println("x=x.setScale : " + a);
		a = a.setScale(997, RoundingMode.HALF_UP);
		pw.println("x=x.setScale(int,RoundingMode) : " + a);
		a = a.plus(new MathContext("precision=742 roundingMode=HALF_EVEN"));
		pw.println("x=x.plus(RoundingMode) : " + a);
		a = a.pow(68, new MathContext("precision=741 roundingMode=CEILING"));
		pw.println("x=x.pow(MathContext) : " + a);
		a = a
				.subtract(
						new BigDecimal(
								"4890987412310000000005648789521631859484894513541534897456123456948745642315647897456123415612315648975644233155555498756423115518915599951111991594456123564894444444447984165555555554118994745613221564448487777756444231231548947822397454"),
						new MathContext("precision=720 roundingMode=UP"));
		pw.println("x=x.subtract(BigDecimal,MathContext) : " + a);


		try {
			a = a.divideToIntegralValue(new BigDecimal("0.6"), new MathContext(
					"precision=74353 roundingMode=HALF_DOWN"));
			pw
			.println("x=x.divideToIntegralValue(BigDecimal,MathContext) : "
					+ a);
		} catch (Throwable e) {

		}

		try {
			a = a.remainder(new BigDecimal("5"), new MathContext(
					"precision=31 roundingMode=UP"));
			pw.println("x=x.remainder(BigDecimal,MathContext) : " + a);
		} catch (Throwable e) {
		}

		
		try {
			bd = bd.setScale(0);
			BigInteger bi = bd.toBigIntegerExact();
			bd = new BigDecimal(bi);
			pw.println("y=y.toBigIntegerExact : " + bd);
			bd = bd.setScale(0);
			bi = bd.toBigInteger();

			bd = new BigDecimal(bi);
			pw.println("y=y.toBigInteger : " + bd);
			a = a.subtract(bd);
			pw.println("x=x.subtract(y) : " + a);
pw.flush();
		} catch (Throwable e) {
		}

		
		return a;
	}
}